        }
        else {
            log(ast.getLineNo(), MSG_SHOULD_BE_SEPARATED, ast.getText());
        }
    }
    if (!hasEmptyLineAfter(ast)) {
        log(nextToken.getLineNo(), MSG_SHOULD_BE_SEPARATED, nextToken.getText());
    }
}

