private static boolean isSingleLineLambda(DetailAST lambda)
{
    boolean result = false;
    final DetailAST block = lambda.getLastChild();
    if (block.getType() != TokenTypes.SLIST) {
        result = lambda.getLineNo() == block.getLineNo();
    }
    return result;
}
