private static boolean isOnTheSameLine(DetailAST ast, int lastStatementEnd,
                                       int forStatementEnd) {
    final boolean onTheSameLine;
    if (lastStatementEnd == ast.getLineNo()
            && forStatementEnd != ast.getLineNo()) {
        onTheSameLine = true;
    }
    else {
        onTheSameLine = false;
    }
    return onTheSameLine;
}
