private static boolean isMultilineStatement(DetailAST ast) {
    final boolean multiline;
    if (ast.getPreviousSibling() != null) {
        final DetailAST prevSibling = ast.getPreviousSibling();
        if (prevSibling.getLineNo() != ast.getLineNo()
                && ast.getParent() != null) {
            multiline = true;
        }
        else {
            multiline = false;
        }
    }
    else {
        multiline = false;
    }
    return multiline;
}
