private void logViolation(String msgKey, DetailAST ast, AbstractFrame frame) {
    if (frame.getFrameName().equals(getNearestClassFrameName())) {
        log(ast, msgKey, ast.getText(), "");
    }
    else {
        log(ast, msgKey, ast.getText(), frame.getFrameName() + '.');
    }
}
