private static boolean isChildOf(DetailAST ast, int type)
{
    boolean result = false;
    DetailAST node = ast;
    do {
        if (node.getType() == type) {
            result = true;
        }
        node = node.getParent();
    } while (node != null && !result);

    return result;
}
