private static boolean isUseOfExternalVariableInsideLoop(DetailAST variable) {
    boolean result = true;
    DetailAST loop2 = variable.getParent();
    while (loop2 != null
        && !isLoopAst(loop2.getType())) {
        loop2 = loop2.getParent();
    }
    return loop2 != null;
}
