private int countMatches(Pattern aPattern, String aTarget)
{
    int matcherCounter = 0;
    final Matcher matcher = aPattern.matcher(aTarget);
    while (matcher.find()) {
        matcherCounter++;
    }
    return matcherCounter;
}
