    final StringBuffer buf = new StringBuffer();
    extractIdent(buf, nameAST);
    final String text = buf.toString();

    if (!getRegexp().match(text)) {
        log(nameAST.getLineNo(),
            "name.invalidPattern",
            text,
            getFormat());
    }
}
