private static String getAbbreviation(String str, int beginIndex, int endIndex) {
    final String result;
    if (endIndex == str.length() - 1) {
        result = str.substring(beginIndex);
    }
    else {
        result = str.substring(beginIndex, endIndex);
    }
    return result;
}
