public void removeTemporaryFolder() throws IOException {
    try (DirectoryStream<Path> paths = Files.newDirectoryStream(temporaryFolder)) {
        for (Path path : paths) {
            Files.delete(path);
        }
    }
    Files.delete(temporaryFolder);
}
