public AccessResult verifyImport(final String forImport) {
    final boolean classMatch;

    if (isRegExp()) {
        classMatch = forImport.matches(className);
    }
    else {
        classMatch = forImport.equals(className);
    }

    return calculateResult(classMatch);
}
