            public int getCurrentIndentation()
            {
                DetailAST curNode = getFirstNode();
                while (curNode.getType() != TokenTypes.IDENT) {
                    curNode = curNode.getFirstChild();
                }
                return curNode.getColumnNo() + getIndentLevel();
            }
        };
    lineWrap.checkIndentation();
}

