{
    DetailAST nodeToVisit = aCurNode.getFirstChild();
    DetailAST currentNode = aCurNode;

    while ((currentNode != null) && (nodeToVisit == null)) {
        nodeToVisit = currentNode.getNextSibling();
        if (nodeToVisit == null) {
            currentNode = currentNode.getParent();
        }
    }
    return nodeToVisit;
}
