private static DetailAST getVarDefStatementSemicolon(DetailAST variableDef)
{
    DetailAST lastNode = variableDef.getLastChild();
    if (lastNode.getType() != TokenTypes.SEMI) {
        lastNode = variableDef.getNextSibling();
    }
    return lastNode;
}
