public String toString() {
    final String result;
    if (levels.cardinality() == 1) {
        result = String.valueOf(levels.nextSetBit(0));
    }
    else {
        final StringBuilder sb = new StringBuilder(50);
        for (int i = levels.nextSetBit(0); i >= 0;
             i = levels.nextSetBit(i + 1)) {
            if (sb.length() > 0) {
                sb.append(", ");
            }
            sb.append(i);
        }
        result = sb.toString();
    }
    return result;
}

