private static Scope calculateScope(final DetailAST ast) {
    final Scope scope;

    if (ScopeUtil.isInInterfaceOrAnnotationBlock(ast)) {
        scope = Scope.PUBLIC;
    }
    else {
        final DetailAST mods = ast.findFirstToken(TokenTypes.MODIFIERS);
        scope = ScopeUtil.getScopeFromMods(mods);
    }
    return scope;
}
