private int findTextStart(String aLine)
{
    int textStart = -1;
    for (int i = 0; i < aLine.length(); i++) {
        if (!Character.isWhitespace(aLine.charAt(i))) {
            if (aLine.regionMatches(i, "/**", 0, 3)) {
                i += 2;
            }
            else if (aLine.regionMatches(i, "*/", 0, 2)) {
                i++;
            }
            else if (aLine.charAt(i) != '*') {
                textStart = i;
                break;
            }
        }
    }
    return textStart;
}
