private void trimTail(StringBuffer aBuffer)
{
    for (int i = aBuffer.length() - 1; i >= 0; i--) {
        if (Character.isWhitespace(aBuffer.charAt(i))) {
            aBuffer.deleteCharAt(i);
        }
        else if ((i > 0)
                 && (aBuffer.charAt(i - 1) == '*')
                 && (aBuffer.charAt(i) == '/'))
        {
            aBuffer.deleteCharAt(i);
            aBuffer.deleteCharAt(i - 1);
            i--;
        }
        else {
            break;
        }
    }
}
