private static int calculateTagColumn(Matcher javadocTagMatcher,
        int lineNumber, int startColumnNumber) {
    int col = javadocTagMatcher.start(1) - 1;
    if (lineNumber == 0) {
        col += startColumnNumber;
    }
    return col;
}
