    for (char c : text.toCharArray()) {
        if (c == '\n') {
            foundCr = false;
            lines++;
            columns = 0;
        }
        else {
            if (foundCr) {
                foundCr = false;
                lines++;
                columns = 0;
            }
            if (c == '\r') {
                foundCr = true;
            }
            columns++;
        }
    }
    if (foundCr) {
        lines++;
        columns = 0;
    }
    return new AbstractMap.SimpleEntry<>(lines, columns);
}

