    return isOnPlainToken(blockComment, TokenTypes.ENUM_DEF, TokenTypes.ENUM)
            || isOnTokenWithModifiers(blockComment, TokenTypes.ENUM_DEF)
            || isOnTokenWithAnnotation(blockComment, TokenTypes.ENUM_DEF);
