private static boolean isDirectoryExcluded(String path, List<Pattern> patternsToExclude) {
    boolean result = false;

    for (Pattern pattern : patternsToExclude) {
        if (pattern.matcher(path).find()) {
            result = true;
            break;
        }
    }

    return result;
}
