private static String generateXpathQuery(DetailAST ast) {
    String xpathQuery = getXpathQuery(null, ast);
    if (!isUniqueAst(ast)) {
        final DetailAST child = findChildWithIdent(ast);
        if (child != null) {
            xpathQuery += "[." + getXpathQuery(ast, child) + ']';
        }
    }
    return xpathQuery;
}
