private static int findPositionAmongSiblings(DetailAST ast) {
    DetailAST cur = ast;
    int pos = 0;
    while (cur != null) {
        if (cur.getType() == ast.getType()) {
            pos++;
        }
        cur = cur.getPreviousSibling();
    }
    return pos;
}
