private static boolean isXpathQueryForNodeIsAccurateEnough(DetailAST ast) {
    return !hasAtLeastOneSiblingWithSameTokenType(ast)
            || XpathUtil.supportsTextAttribute(ast)
            || findChildWithTextAttribute(ast) != null;
}
