private boolean isMessageMatch(AuditEvent event) {
    boolean match = true;
    if (eventMessageRegexp != null) {
        final Matcher messageMatcher = eventMessageRegexp.matcher(event.getMessage());
        match = messageMatcher.find();
    }
    return match;
}
