    boolean match = true;
    if (tagIdRegexp != null) {
        if (event.getModuleId() == null) {
            match = false;
        }
        else {
            final Matcher idMatcher = tagIdRegexp.matcher(event.getModuleId());
            match = idMatcher.find();
        }
    }
    return match;
}

