private boolean isIdMatch(TreeWalkerAuditEvent event) {
    boolean match = false;
    if (event.getModuleId() != null) {
        final Matcher idMatcher = tagCheckRegexp.matcher(event.getModuleId());
        match = idMatcher.find();
    }
    return match;
}
