private boolean isMessageMatch(TreeWalkerAuditEvent event) {
    final boolean match;
    if (tagMessageRegexp == null) {
        match = true;
    }
    else {
        final Matcher messageMatcher = tagMessageRegexp.matcher(event.getMessage());
        match = messageMatcher.find();
    }
    return match;
}
