private TreePath makeCodeSelection() {
    final TreePath selected = tree.getSelectionPath();
    final DetailAST ast = (DetailAST) selected.getLastPathComponent();
    new CodeSelector(ast, editor, linePositionMap).select();
    return selected;
}
