public void shutdownAndWait() {
  shouldRun = false;
  slowDiskDetectionDaemon.interrupt();
  try {
    slowDiskDetectionDaemon.join();
  } catch (InterruptedException e) {
    LOG.error("Disk Outlier Detection daemon did not shutdown", e);
  }
}
