public void updateWithReplica(StorageLocation replicaLocation) {
  // for local replicas, the replica location is assumed to be a file.
  File diskFile = replicaLocation.getFile();
  if (null == diskFile) {
    setDirInternal(null);
  } else {
    setDirInternal(diskFile.getParentFile());
  }
}
