private void sleepAfterException() {
  try {
    long sleepTime = Math.min(1000, dnConf.heartBeatInterval);
    Thread.sleep(sleepTime);
  } catch (InterruptedException ie) {
    Thread.currentThread().interrupt();
  }
}
