private void releaseWaiters() {
  Waiter head;
  do {
    head = waiters;
  } while (!ATOMIC_HELPER.casWaiters(this, head, Waiter.TOMBSTONE));
  for (
      Waiter currentWaiter = head;
      currentWaiter != null;
      currentWaiter = currentWaiter.next) {
    currentWaiter.unpark();
  }
}
