public sun.misc.Unsafe run() throws Exception {
  Class<sun.misc.Unsafe> k = sun.misc.Unsafe.class;
  for (java.lang.reflect.Field f : k.getDeclaredFields()) {
    f.setAccessible(true);
    Object x = f.get(null);
    if (k.isInstance(x)) {
      return k.cast(x);
    }
  }
  throw new NoSuchFieldError("the Unsafe");
}
