public static void throwIfUnchecked(Throwable throwable) {
  checkNotNull(throwable);
  if (throwable instanceof RuntimeException) {
    throw (RuntimeException) throwable;
  }
  if (throwable instanceof Error) {
    throw (Error) throwable;
  }
}
