public void onFailure(@Nonnull Throwable t) {
  Throwable exception = (t instanceof ExecutionException) ?
      t.getCause() : t;
  LOG.warn("Exception running disk checks against volume " +
      reference.getVolume(), exception);
  markFailed();
  cleanup();
}
