int[] getRealTargetIndices() {
  int realTargets = getRealTargets();
  int[] results = new int[realTargets];
  int m = 0;
  for (int i = 0; i < targets.length; i++) {
    if (targetsStatus[i]) {
      results[m++] = targetIndices[i];
    }
  }
  return results;
}
