void close() {
  for (StripedBlockWriter writer : writers) {
    ByteBuffer targetBuffer = writer.getTargetBuffer();
    if (targetBuffer != null) {
      reconstructor.freeBuffer(targetBuffer);
      writer.freeTargetBuffer();
    }
  }

  for (int i = 0; i < targets.length; i++) {
    writers[i].close();
  }
}

