public static byte[] longToByte(long v) {
  byte[] data = new byte[8];
  data[0] = (byte) (v >>> 56);
  data[1] = (byte) (v >>> 48);
  data[2] = (byte) (v >>> 40);
  data[3] = (byte) (v >>> 32);
  data[4] = (byte) (v >>> 24);
  data[5] = (byte) (v >>> 16);
  data[6] = (byte) (v >>> 8);
  data[7] = (byte) (v >>> 0);
  return data;
}
