private void clearFlag(long flag) {
  long prev;
  do {
    prev = unsafe.getLongVolatile(null, this.slotAddress);
    if ((prev & flag) == 0) {
      return;
    }
  } while (!unsafe.compareAndSwapLong(null, this.slotAddress,
              prev, prev & (~flag)));
}

