void addPeer(Peer peer, Thread t, DataXceiver xceiver)
    throws IOException {
  lock.lock();
  try {
    if (closed) {
      throw new IOException("Server closed.");
    }
    peers.put(peer, t);
    peersXceiver.put(peer, xceiver);
    datanode.metrics.incrDataNodeActiveXceiversCount();
  } finally {
    lock.unlock();
  }
}

