private boolean waitAllPeers(long timeout, TimeUnit unit) {
  long nanos = unit.toNanos(timeout);
  lock.lock();
  try {
    while (!peers.isEmpty()) {
      if (nanos <= 0L) {
        return false;
      }
      nanos = noPeers.awaitNanos(nanos);
    }
  } catch (InterruptedException e) {
    LOG.debug("Interrupted waiting for peers to close");
    return false;
  } finally {
    lock.unlock();
  }
  return true;
}
