public synchronized void removeAllVolumeScanners() {
  for (Entry<String, VolumeScanner> entry : scanners.entrySet()) {
    entry.getValue().shutdown();
  }
  for (Entry<String, VolumeScanner> entry : scanners.entrySet()) {
    Uninterruptibles.joinUninterruptibly(entry.getValue(),
        5, TimeUnit.MINUTES);
  }
  scanners.clear();
}
