synchronized boolean packetSentInTime() {
  long diff = Time.monotonicNow() - lastSentTime;
  if (diff > maxSendIdleTime) {
    LOG.info("A packet was last sent " + diff + " milliseconds ago.");
    return false;
  }
  return true;
}
