public boolean isAnchored() {
  long prev = unsafe.getLongVolatile(null, this.slotAddress);
  if ((prev & VALID_FLAG) == 0) {
    // Slot is no longer valid.
    return false;
  }
  return ((prev & 0x7fffffff) != 0);
}
