private String getVolumeBaseUri() {
  final ReplicaInfo ri = replicaInfo.getReplicaInfo();
  if (ri != null && ri.getVolume() != null) {
    return ri.getVolume().getBaseURI().toString();
  }
  return "unavailable";
}
