  synchronized (ackQueue) {
    while (isRunning() && !ackQueue.isEmpty()) {
      try {
        ackQueue.wait();
      } catch (InterruptedException e) {
        running = false;
        Thread.currentThread().interrupt();
      }
    }
    LOG.debug("{}: closing", this);
    running = false;
    ackQueue.notifyAll();
  }

  synchronized (this) {
    running = false;
    notifyAll();
  }
}

