public URI getBpURI(String bpid, String currentStorageDir) {
  try {
    File localFile = new File(getUri());
    return new File(new File(localFile, currentStorageDir), bpid).toURI();
  } catch (IllegalArgumentException e) {
    return null;
  }
}
